// story chapter
function prologuechosen(game){

	game.peoplerp.yarra.initial = 30;
	game.peoplerp.qum.initial = 50;
	game.peoplerp.aka.initial = 20;
	game.peoplerp.aka.cap = 99;

    rpscriptstart(game.peoplerp);

	if(document.getElementById("kay-sword").checked === true){
		game.globalstat.shiningword += 1;
	}

	game.simonstat.bonustat.atk += 5;
	game.simonstat.bonustat.def += 5;
	game.simonstat.bonustat.mag += 5;
	game.simonstat.bonustat.mdf += 5;
	game.simonstat.bonustat.sp = 2;

	var yarramanipulate = 0;

	if(document.getElementById("old-grave").checked === true){
		yarramanipulate += 1;
	}

	if(document.getElementById("kara-dialogue").checked === true){
		yarramanipulate += 1;
	}

	if(document.getElementById("guard-qum").checked === true){
		yarramanipulate += 1;
	}

	if(document.getElementById("fuck-qum").checked === true){
		yarramanipulate += 5;
	}

	if(document.getElementById("priestess-dialogue").checked === true){
		yarramanipulate += 2;
	}

	if(yarramanipulate >= 1){
		document.getElementById("house-enable").style.display = "";
		if(document.getElementById("simon-house").checked === true){
			yarramanipulate += 2;
		}
	} else {
		document.getElementById("house-enable").style.display = "none";
	}

	if(yarramanipulate >= 4){
		document.getElementById("sleep-enable").style.display = "";
		if(document.getElementById("simon-sleep").checked === true){
			yarramanipulate += 2;
		}
	} else{
		document.getElementById("sleep-enable").style.display = "none";
	}

	if(yarramanipulate >= 12){
		document.getElementById("manipulation-low").style.display = "none";
		game.peoplerp.yarra.rp += (yarramanipulate-12);
	} else {
		document.getElementById("manipulation-low").style.display = "";
	}

	var cleareffort = 0;

	var monsterfight = +document.getElementById("monster-group").value;
	var logroad = +document.getElementById("logs-clear").value;

	if(document.getElementById("chimera-fight").checked === true){
		cleareffort += 10;
	}

	cleareffort += monsterfight*2 + logroad;

	if(cleareffort >= 20){
		game.peoplerp.yarra.rp += 1;
		game.peoplerp.qum.rp += 1;
		game.peoplerp.aka.rp += 1;
		document.getElementById("clear-low").style.display = "none";
	} else if(cleareffort >= 15){
		document.getElementById("clear-low").style.display = "none";
	} else{
		document.getElementById("clear-low").style.display = "";
	}

    stampendvar("Shining Swords: ", game.globalstat.shiningword, "stamp-shining-sword-tal", true);

    stampartialrp("Yarra RP: ", game.peoplerp.yarra, "stamp-yarra-rp-tal");
    stampartialrp("Qum RP: ", game.peoplerp.qum, "stamp-qum-rp-tal");
    stampartialrp("Aka RP: ", game.peoplerp.aka, "stamp-aka-rp-tal");

    stampendvar("HP: +", game.simonstat.bonustat.hp, "stamp-stat-hp-tal", true);
    stampendvar("MP: +", game.simonstat.bonustat.mp, "stamp-stat-mp-tal", true);
    stampendvar("ATK: +", game.simonstat.bonustat.atk, "stamp-stat-atk-tal", true);
    stampendvar("DEF: +", game.simonstat.bonustat.def, "stamp-stat-def-tal", true);
    stampendvar("MAG: +", game.simonstat.bonustat.mag, "stamp-stat-mag-tal", true);
    stampendvar("MDF: +", game.simonstat.bonustat.mdf, "stamp-stat-mdf-tal", true);
    stampendvar("AGI: +", game.simonstat.bonustat.agi, "stamp-stat-agi-tal", true);
    stampendvar("LUK: +", game.simonstat.bonustat.luk, "stamp-stat-luk-tal", true);
    stampendlust(game.simonstat.bonustat.sp, "stamp-stat-sp-tal", true);
}

