function stampendvar(name, score, element, execute){

	if(execute === true){
        document.getElementById(element).style.display = "";
        document.getElementById(element).textContent = "" + name + score;
	} else {
        document.getElementById(element).style.display = "none";
	}
}

function stampenditem(item, element){

    if(item === true){
        document.getElementById(element).style.display = "";
    } else {
        document.getElementById(element).style.display = "none";
    }
}

function stampartialrp(name, person, element){

	if(person.stamp === true){
        document.getElementById(element).style.display = "";
		if(person.lock === false){
			var total = person.initial + person.rp;
            var change = total - person.starter;
            if(change > 0){
                document.getElementById(element).textContent = "" + name + " " + total + " (+" + change + ")";
                document.getElementById(element).style.color = "#00FF00";
            } else if(change < 0){
                document.getElementById(element).textContent = "" + name + " " + total + " (" + change + ")";
                document.getElementById(element).style.color = "#FF0000";
            } else {
                document.getElementById(element).textContent = "" + name + " " + total;
                document.getElementById(element).style.color = "";
            }
        } else {
            document.getElementById(element).textContent = "" + name + " Maxed";
            document.getElementById(element).style.color = "";
        }

	} else {
        document.getElementById(element).style.display = "none";
	}
}

function stampartialvalue(name, number, element){

    if(number.stamp === true){
        document.getElementById(element).style.display = "";
        var change = number.value - number.starting;
        if(change > 0){
            document.getElementById(element).textContent = "" + name + " " + number.value + " (+" + change + ")";
            document.getElementById(element).style.color = "#00FF00";
        } else if(change < 0){
            document.getElementById(element).textContent = "" + name + " " + number.value + " (" + change + ")";
            document.getElementById(element).style.color = "#FF0000";
        } else {
            document.getElementById(element).textContent = "" + name + " " + number.value;
            document.getElementById(element).style.color = "";
        }
    } else {
        document.getElementById(element).style.display = "none";
    }
}

