function stampendlust(score, element, execute){

	if(execute === true){
        document.getElementById(element).style.display = "";
		if(score === 2){
            document.getElementById(element).textContent = "Starting SP: 0, SP per Turn: 1";
		} else if(score === 3){
            document.getElementById(element).textContent = "Starting SP:0, SP per Turn: 2";
		} else if(score === 4){
            document.getElementById(element).textContent = "Starting SP:10, SP per Turn: 5";
		} else if(score === 5){
            document.getElementById(element).textContent = "Starting SP: 20, SP pr Turn: 5 SP Gain on HP/MP Damage: 5SP";
		}
	} else {
        document.getElementById(element).style.display = "none";
	}
}

