function lockingrp(person){

	var total = person.initial + person.rp; 
	if(person.cap !== 101){
		if(person.cap !== 99){
			if(person.lock === false){
				if(total >= 100){
					person.lock = true;
				}
			}
		} else{
			if(total >= 100){
				person.rp = 99 - person.initial;
			}
		}
	} else {
		if(total >= 100){
			person.rp = 100 - person.initial;
		}
	}
}

